import settings from '../settings';

export default {
  template: '<div><span v-if="login">正在跳转...</span><span v-else>看到此消息时，说明项目中没有正确配置登录地址</span></div>',
  data() {
    return {
      login: null
    }
  },
  created() {
    this.login = settings.urls['login'];
    let url = this.login;
    if (url) {
      let redirect = this.$route.query.redirect;
      url = url + '?redirect=' + redirect;
      window.location.href = url;
    }
  }
}