import vuex from 'vuex'
import fxdata from './data'

export default {
  install: function (Vue, options = {}) {
    Vue.use(vuex);

    let store = options.store;
    if(!store) {
      store = new vuex.Store({
        modules: {
          fxdata
        }
      });
      options.store = store;
    } else {
      store.registerModule("fxdata", fxdata);
    }
  }
}
